import React from 'react';
import axios from 'axios';

export function getSingup(props) {
   let authData = {
      email: props.email,
      name: props.name,
      password: props.password,
      returnSecureToken: true
    }
  console.log('signup');
  if (authData.email != '') {
    axios.post('https://ekreative-json-server.herokuapp.com/users', authData)
      .then(response => this.setState({ authData: response.data.id }));
  }
}
export function getLogin (props) {
    let authData = {
      email: props.email,
      password: props.password,
      returnSecureToken: true
    }

  var myTest;

  axios.get('https://ekreative-json-server.herokuapp.com/users?email=' + authData.email)
    .then(response => myTest = response.data);

  console.log(myTest);
}
