import React from "react";
import { Button, Input } from ".";
import '../scss/app.scss'

function SingupForm({Singup, error}) {

  const [details, setDetails] = React.useState({
    name: {
      type: 'firs and last name',
      value: '',
      label: 'first and last name'
    },
    email: {
      type: 'email',
      value: '',
      label: 'email'
    },
    password: {
      type: 'password',
      value: '',
      label: 'password'
    },
    confirmPassword: {
      type: 'password',
      value: '',
      label: 'confirmPassword'
    },
    age: {
      type: 'age',
      value: '',
      label: 'age'
    },
    avatar: {
      type: 'url',
      value: '',
      label: 'avatar'
    }
  })

  const submitHendler = e => {
    e.preventDefault();

    Singup(details)
  }

  const renderInput = () => {
    return Object.keys(details).map((dataIput, index) => {
      const data = details[dataIput]

      return (
        <Input
          type={data.type}
          value={data.value}
          label={data.label}
        />
      )
    })
  }

  return (
    <div className='authorization'>
          <div className="form">
              <h2>Sing Up</h2>
        <form onSubmit={ submitHendler }>{ renderInput() }</form>
      </div>
      <div className="block-of-buttons">
          <Button
            value="Sing Up"
            className="singUp"
            // disabled={!state.isFormValid}
          />
          <Button
            value="Log In"
            className="logIn"
            // disabled={!state.isFormValid}
          />
      </div>
    </div>
  );
}


export default SingupForm;
