import React from "react";
import { useDispatch } from "react-redux";
import { setGetPostsPage } from "../redux/action";
import "../scss/components/pagination.scss";

const Pagination = ({ postsPerPage, totalPosts }) => {
    const [page, setPage] = React.useState({})
  const pageNumbers = [];
  const dispatch = useDispatch();

  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    pageNumbers.push(i);
  }

  fetch(
    `https://ekreative-json-server.herokuapp.com/664/posts?_page=${page.number}&_limit=${postsPerPage}`
  )
    .then((response) => response.json())
    .then((data) => {
      dispatch(setGetPostsPage(data));
    });

  return (
    <div className="pagination">
      <ul>
        {pageNumbers.map((number) => (
          <li className="page-item" key={number}>
            <a className="page-link" onClick={setPage({number: number})}>{number}</a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Pagination;
