import React from "react";
import "./../scss/app.scss";
import { Input, Button } from "../conponents/index";
import { Link } from "react-router-dom";
import { getSingup } from '../conponents/server'


function Singup() {
  const [user, setUser] = React.useState({
    username: '',
    email: '',
    password: ''
  });

  const changeHandler = (e) => {
    const { label, value } = e.target
    setUser(prevState => ({
      ...prevState,
      [label]: value
    }))
  }

  function handleChange (event) {
    setUser({value: event.target.value});
  }

  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form>
          <Input
            type="text"
            value={user.username}
            onChange={(event) => handleChange(event)}
            label="fullname"
            autoComplete='off'
          />
          <Input
            type="text"
            value={user.email}
            onChange={changeHandler}
            label="email"
          />
          <Input
            type="password"
            value={user.password}
            onChange={changeHandler}
            label="password"
          />
        </form>
      </div>
      <Button
        type="submit"
        onClick={getSingup(user)}
      >
        Singup
      </Button>
    </div>
  );
}

export default Singup;
