import React from "react";
import "./../scss/app.scss";
import { Input, Button } from "../conponents/index";
import { Link } from "react-router-dom";
import axios from "axios";


function Singup() {
  const [user, setUser] = React.useState({
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    confirPassword: '',
    age: '',
    age: '',
    avatar: '',
  });

  function getSingup() {
    const authData = {
      email: user.email,
      password: user.password,
      firstname: user.firstname,
      lastname: user.lastname,
      age: user.age,
      avatar: user.avatar,
    }
    axios.post('https://ekreative-json-server.herokuapp.com/users', authData)
}

  function handleChange (event) {
    setUser({value: event.target.value});
  }

  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form>
          <Input
            type="text"
            value={user.username}
            onChange={handleChange}
            label="fullname"
            autoComplete='off'
          />
          <Input
            type="text"
            value={user.email}
            onChange={handleChange}
            label="email"
          />
          <Input
            type="password"
            value={user.password}
            onChange={handleChange}
            label="password"
          />
          <Button
            type='button'
        onClick={getSingup}
      >
        Singup
      </Button>
        </form>
      </div>
    </div>
  );
}

export default Singup;
