export const ADD_DATA_TO_EDIT = "EDIT";
export const DATA_USER = "DATA_USER";
export const DATA_COMMENT = "DATA_COMMENT";
export const GET_POSTS_PAGE = "GET_POSTS_PAGE";

export const setAddData = (id, title, body, userId, createdAt) => ({
  type: ADD_DATA_TO_EDIT,
  id,
  title,
  body,
  userId,
  createdAt,
});

export const setDataUser = ({
  id,
  email,
  firstname,
  lastname,
  age,
  avatar,
}) => ({
  type: DATA_USER,
  id,
  email,
  firstname,
  lastname,
  age,
  avatar,
});

export const setDataComment = ({
  id,
  postId,
  body,
  userId,
  createAt,
  updateAt,
}) => ({
  type: DATA_COMMENT,
  id,
  postId,
  body,
  userId,
  createAt,
  updateAt,
});

export const setGetPostsPage = async (number, postsPerPage) => {
  return function (dispatch) {
    const response = fetch(
      `https://ekreative-json-server.herokuapp.com/664/posts?_page=${number}&_limit=${postsPerPage}`
    )
      .then((response) => response.json())
      .then((data) => {
        return data;
      });

    console.log(response);
    return dispatch({
      type: GET_POSTS_PAGE,
      posts: response,
    });
  };
};
