export const ADD_DATA_TO_EDIT = "ADD_DATA_TO_EDIT";
export const DATA_USER = "DATA_USER";
export const GET_POSTS_PAGE = "GET_POSTS_PAGE";
export const ANNOUN_USER = "ANNOUN_USER";
export const DATA_EDIT_ANNOUN = "DATA_EDIT_ANNOUN";
export const DATA_OF_POST = "DATA_OF_POST";
export const POSTS = "POSTS";
export const UPDATE_POST = "UPDATE_POST";
export const DATA_ANNOUN = 'DATA_ANNOUN'
export const DATA_DELETE_ANNOUN = 'DATA_DELETE_ANNOUN'
export const DATA_CREATE_ANNOUN = 'DATA_CREATE_ANNOUN'

export const setAddData = ({ id, title, body, userId, createdAt, key }) => ({
  type: ADD_DATA_TO_EDIT,
  id,
  title,
  body,
  userId,
  createdAt,
  key,
});

export const setDataUser = ({
  id,
  email,
  firstname,
  lastname,
  age,
  avatar,
}) => ({
  type: DATA_USER,
  id,
  email,
  firstname,
  lastname,
  age,
  avatar,
});

export const setGetPostsPage = (response, crrentPge, postPerPage) => async (dispatch) => {
  const response = fetch(
      `https://ekreative-json-server.herokuapp.com/posts?_page=${currentPage}&_limit=${postsPerPage}`
    )
      .then((response) => response.json())
      .then((data) => {
        return data
      });


  dispatch({
    type: GET_POSTS_PAGE,
    payload: response,
  });
};

export const setAnnounUser = (userId) => async (dispatch) => {
  // const header = {"Content-Type": "application/json"}
  const response = await fetch(
    `https://ekreative-json-server.herokuapp.com/users/${userId}`
  )
    .then((response) => response.json())
    .then((data) => {
      return data;
    });

  dispatch({
    type: ANNOUN_USER,
    payload: response,
  });
};

export const setDataEditAnnoun = (data) => ({
  type: DATA_EDIT_ANNOUN,
  payload: data,
});

export const setDataDeleteAnnoun = (data) => ({
  type: DATA_DELETE_ANNOUN,
  payload: data,
});

export const setDataCreateAnnoun = (data) => ({
  type: DATA_CREATE_ANNOUN,
  payload: data,
});

export const setDataOfPost = (dataPost) => ({
    type: DATA_OF_POST, payload: dataPost
  })

export const setPosts = (posts) => ({
  type: POSTS,
  payload: posts,
});

export const updatePostData = (updatedPost) => ({
  type: UPDATE_POST,
  payload: updatedPost,
});

export const setDataAnnouncements = (data) => ({
  type: DATA_ANNOUN,
  payload: data
})
