import React from "react";
import "./../scss/app.scss";
import { Input } from "../conponents/index";

function validateEmail(email) {
  const re =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
}

function Singup() {
  const [user, setUser] = React.useState({
    formData: {
      fistname: {
        type: "name",
        value: "",
        label: "first name",
        errorMessage: "firstname cannot be empty",
        touched: false,
        valid: false,
      },
      lastname: {
        type: "name",
        value: "",
        label: "last name",
        errorMessage: "lastname cannot be empty",
        valid: false,
        touched: false,
      },
      email: {
        type: "email",
        value: "",
        label: "email",
        errorMessage: "put correct email",
        valid: false,
        touched: false,
        validation: {
          required: true,
          email: true,
        },
      },
      password: {
        type: "password",
        value: "",
        label: "password",
        errorMessage: "password must have least 9 signs",
        valid: false,
        touched: false,
      },
      confirmPassword: {
        type: "password",
        value: "",
        label: "confirmation password",
        errorMessage: "put correct password",
        valid: false,
        touched: false,
      },
      age: {
        type: "number",
        value: "",
        label: "age",
        errorMessage: "age cannot be empty",
        valid: false,
        touched: false,
      },
      avatar: {
        type: "url",
        value: "",
        label: "avatar",
        errorMessage: "avatar cannot be empty",
        valid: false,
        touched: false,
      },
    },
  });

  const validateControl = (value, validation) => {
    if (!validation) {
      return true;
    }
    let isValid = true;

    if (validation.required) {
      isValid = value.trim() !== "" && isValid;
    }
    if (validation.email) {
      isValid = validateEmail(value) && isValid;
    }
    if (validation.minLength) {
      isValid = value.length >= validation.minLength && isValid;
    }
    return isValid;
  };


  const onChangeHandler = (event, dataName) => {
    const formData = { ...user.formData };
    const data = { ...formData[dataName] };

    data.value = event.target.value;
    data.touched = true;
    data.valid = validateControl(data.value, data.validation);

    formData[dataName] = data;

    let isFormValid = true;

    Object.keys(formData).forEach((name) => {
      isFormValid = formData[name].valid && isFormValid;
    });

    setUser((prevState) => ({
      ...prevState,
      formData,
      isFormValid,
    }));
  };

  const renderInputs = () => {
    return Object.keys(user.formData).map((dataName, index) => {
      const data = user.formData[dataName];
      return (
        <Input
          key={dataName + index}
          type={data.type}
          value={data.value}
          valid={data.valid}
          touched={data.touched}
          label={data.label}
          shouldValidate={!!data.validation}
          errorMessage={data.errorMessage}
          onChange={(event) => onChangeHandler(event, dataName)}
        />
      );
    });
  };

  return (
    <div className="authorization">
        <h2>Sing Up</h2>
      <div className="form">
        <form>{renderInputs()}</form>
      </div>
    </div>
  );
}

export default Singup;
