import React from "react";
import Input from "../conponents/Input";

const [state, setState] = React.useState({
    isFormValid: false,
    formData: {
        userName: {
            value: '',
            type: 'text',
            label: 'first and last name',
            errorMessage: 'fill in the field',
            valide: false,
            touched: false,
        },
        email: {
            value: '',
            type: 'email',
            label: 'email',
            errorMessage: 'enter correct email',
            valide: false,
            touched: false,
        },
        password: {
            value: '',
            type: 'text',
            label: 'password',
            errorMessage: 'enter correct password',
            valide: false,
            touched: false,
        },
        confirmationPassword: {
            value: '',
            type: 'text',
            label: 'confirmation password',
            errorMessage: 'enter correct password',
            valide: false,
            touched: false,
        },
        age: {
            value: '',
            type: 'number',
            label: 'age',
            errorMessage: 'fill in the field',
            valide: false,
            touched: false,
        },
        avatar: {
            value: '',
            type: 'url',
            label: 'avatar',
            errorMessage: 'enter correct url',
            valide: false,
            touched: false,
        }
    }
})

const renderInputs = () => {
    return (
        <Input value={state.value}/>
    )
}

function Authorization() {
  return (
    <div className='authurization'>
      <div className="from">
        <form>
            {renderInputs()}
        </form>
      </div>
    </div>
  );
}

export default Authorization;
