import React from "react";
import "./../scss/app.scss";
import { SingupForm, LoginForm } from '../conponents/index';


function Authorization() {

  const adminUser = {
    email: 'admin@admin.com',
    password: 'admin'
  }

  const [user, setUser] = React.useState({ name: '', email: '', password: '', confirmPassword: '', age: '', avatar: '' })
  const [error, setError] = React.useState('')

  const Login = details => {
    console.log(details);

    if (details.email == adminUser.email && details.password == adminUser.password) {
      console.log('Logged in');

      setUser({
        name: details.name,
        email: details.email
      })
    } else {
      console.log('Details do not match!');
    }
  }

  const Logout = () => {
    console.log('Logout');
  }

  const Singup = () => {
    console.log('Singup');
  }



  return (
    <div className="authorization">
      <h1>Authorization</h1>
      {
        true
          ? <LoginForm Login={Login} error={error}  />
          : <SingupForm Singup={Singup} error={error}/>
        }
    </div>
  );
}

export default Authorization;
