import { combineReducers, createStore } from "redux";
import thunk from 'redux-thunk'
import { postReducer, userReducer, commentReducer, paginateReducer } from "./reducer";

const rootReducer = combineReducers({ postReducer, userReducer, commentReducer, paginateReducer })

const store = createStore(
  rootReducer
)

export default store
