import React from "react";
import { Button, Input } from ".";
import "../scss/app.scss";

function validateEmail(email) {
  const re =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
}

function SingupForm({ user, onChange }) {
  // const submitHendler = (e) => {
  //   e.preventDefault();
  // };

  const submitHendler = () => {
    let message = [];

    if (user.name.value === "") {
      message.push(user.name.errorMessage);
    }

    if (user.email.value !== validateEmail()) {
      message.push(user.email.errorMessage);
    }

    if (user.password.value.length < 10) {
      message.push(user.password.errorMessage);
    }

    if (user.confirmPassword.value !== user.password.value) {
      message.push(user.confirmPassword.errorMessage);
    }

    if (user.age.value === "") {
      message.push(user.age.errorMessage);
    }

    if (user.avatar.value === "") {
      message.push(user.avatar.errorMessage);
    }
  };

  const onChangeHendler = (event, dataInput) => {
    const data = { ...user[dataInput]};

    data.value = event.target.value;
    data.touched = true;

    user[dataInput] = data;

  }

  const renderInput = () => {
    return Object.keys(user).map((dataInput, index) => {
      const data = user[dataInput];

      return <Input
        key={dataInput + index}
        type={data.type}
        value={data.value}
        label={data.label}
        errorMessage={data.errorMessage}
        touched={data.touched}
        onChange={onChange}
      />;
    });
  };

  return (
    <div className="authorization">
      <div className="form">
        <h2>Sing Up</h2>
        <form onSubmit={submitHendler}>{renderInput()}</form>
      </div>
      <div className="block-of-buttons">
        <Button
          value="Sing Up"
          className="singUp"
          type="submit"
          // disabled={!state.isFormValid}
        />
        <Button
          value="Log In"
          className="logIn"
          // disabled={!state.isFormValid}
        />
      </div>
    </div>
  );
}

export default SingupForm;
