import React from "react";
import { Button, Input } from ".";
import '../scss/app.scss'

function LoginForm({ Login, error }) {

  const [details, setDetails] = React.useState({
    email: {
      type: 'email',
      value: '',
      label: 'email'
    },
    password: {
      type: 'password',
      value: '',
      label: 'password'
    }
  })

  const submitHendler = e => {
    e.preventDefault();

    Login(details)
  }

  const renderInput = () => {
    return Object.keys(details).map((dataIput, index) => {
      const data = details[dataIput]

      return (
        <Input
          type={data.type}
          value={data.value}
          label={data.label}
          onChange={e => setDetails({...details,  value: e.target.value})} value={details.name}
        />
      )
    })
  }

  return (
    <div className='authorization'>
      <div className="form">
          <h2>Log In</h2>
        <form onSubmit={submitHendler}>
          {renderInput()}
        </form>
      </div>
      <div className="block-of-buttons">
          <Button
            value="Log In"
            className="singUp"
          />
          <Button
            value="Sing Up"
            className="logIn"
          />
      </div>
    </div>
  );
}

export default LoginForm;
