import axios from "axios";
import React from "react";
import { Button, UpdatePost } from ".";
import "../scss/components/postBlock.scss";

import { Route, Link, NavLink } from "react-router-dom";
import classNames from "classnames";
import { setAddData } from "../redux/action";

const PostBlock = ({ id, title, body, userId, createdAt }) => {
  const [usersData, setUsersData] = React.useState([]);
  const [comments, setComments] = React.useState([]);
  const [dataComments, setDataComments] = React.useState({});
  const [userDataComments, setUserDataComments] = React.useState([]);
  const dispatch = useDispatch()

  React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUsersData(data);
      });
  }, []);


  React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/comments?postId=${id}&_sort=createdAt&_order=asc`
    )
      .then((response) => response.json())
      .then((data) => {
        setComments(data);
      });
  }, []);

  //  comments.map((comment) => {
  //   setDataComments({
  //     // id: comment.id,
  //     // postId: comment.postId,
  //     userId: comment.userId,
  //     // createdAt: comment.createdAt,
  //     // updateAt: comment.updateAt,
  //   });
  //           console.log(comment.userId);
  // });
// console.log(dataComments.userId);

  // React.useEffect(() => {
  // fetch(`https://ekreative-json-server.herokuapp.com/users/${dataComments.userId}`)
  //   .then((response) => response.json())
  //   .then((data) => {
  //       setUserDataComments(data);
  // console.log('user data of comments', data);

  //   });
  // }, []);

  function deletePost() {
    axios.delete(
      `https://ekreative-json-server.herokuapp.com/664/post/${userId}`
    );
  }

  const editPost = () => {
    dispatch(setAddData({id, title, body, userId, createdAt}))
  }

  return (
    <div className="post-block">
      <div className="content-post">
        <div className="content">
        <h3>{title}</h3>
        <p>{body}</p>
        <p>
          {usersData.firstname} {usersData.lastname}
          </p>
          </div>
        <div className="content-button">
        <button className={classNames('button-post', 'button')} onClick={editPost} >
          Edit
        </button>
        <button className={classNames('button-post', 'button')} onClick={deletePost}>
          Delete
        </button>
          </div>
      </div>

      <div className="comments-of-post">
        {/* <h3>Comments</h3> */}
        {comments.map((comment) => (
          <div key={comment.id}>
            <p>{}</p>
            <p className="comment">{comment.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostBlock;
