import React from "react";
import { Input, Button } from "../conponents/index";
import "./../scss/app.scss";
import { Link } from 'react-router-dom'
import axios from "axios";

function validateEmail(email) {
  const re =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
}

function Authorization() {
  const [state, setState] = React.useState({
    isFormValid: false,
    formData: {
      userName: {
        value: "",
        type: "text",
        label: "first and last name",
        errorMessage: "fill in the field",
        valide: false,
        touched: false,
        validation: {
          required: true,
          userName: true,
        },
      },
      email: {
        value: "",
        type: "email",
        label: "email",
        errorMessage: "enter correct email",
        valide: false,
        touched: false,
        validation: {
          required: true,
          email: true,
        },
      },
      password: {
        value: "",
        type: "password",
        label: "password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          password: 9,
        },
      },
      confirmationPassword: {
        value: "",
        type: "password",
        label: "confirmation password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          confirmationPassword: true,
        },
      },
      age: {
        value: "",
        type: "number",
        label: "age",
        errorMessage: "fill in the field",
        valide: false,
        touched: false,
        validation: {
          required: true,
          age: true,
        },
      },
      avatar: {
        value: "",
        type: "url",
        label: "your avatar",
        errorMessage: "enter correct url",
        valide: false,
        touched: false,
      },
    },
  });

  const registrHendler = async () => {
    const authData = {
      userName: state.formData.userName.value,
      email: state.formData.email.value,
      password: state.formData.password.value,
      confirmationPassword: state.formData.confirmationPassword.value,
      age: state.formData.age.value,
      avatar: state.formData.avatar.value,
      returnSecureToken: true
    }

     axios.post('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyABuCCU2ip1w5leYsAv2F8YTEt0G8B66Sw', authData)
      .catch((e) => {
        console.log(e);
      })
  }

  const submitHendler = (event) => {
    event.preventDefault();
  };

  const validateData = (value, validation) => {
    if (!validation) {
      return true;
    }

    let isValid = true;

    if (validation.required) {
      isValid = value.trim() !== "" && isValid;
    }
    if (validation.userName) {
      isValid = value.trim() !== "" && isValid;
    }
    if (validation.email) {
      isValid = validateEmail(value) && isValid;
    }
    if (validation.password) {
      isValid = value.length >= validation.password && isValid;
    }
    if (validation.confirmationPassword) {
      isValid = value === value.password && isValid;
    }
    if (validation.age) {
      isValid = value.trim() !== "" && isValid;
    }
    return isValid;
  };

  const changeInput = (event, controlData) => {
    const formData = { ...state.formData };
    const data = { ...formData[controlData] };

    data.value = event.target.value;
    data.touched = true;
    data.valide = validateData(data.value, data.validation);

    formData[controlData] = data;
    let isFormValid = true;

    Object.keys(formData).forEach((name) => {
      isFormValid = formData[name].valid && isFormValid;
    });

    setState((prevState) => ({
      ...prevState,
      formData,
      isFormValid,
    }));
  };

  const renderInputs = () => {
    return Object.keys(state.formData).map((controlData, index) => {
      const data = state.formData[controlData];

      return (
        <Input
          key={controlData + index}
          value={data.value}
          type={data.type}
          label={data.label}
          errorMessage={data.errorMessage}
          valide={data.valide}
          shouldValidate={!!data.validation}
          touched={data.touched}
          onChange={(event) => changeInput(event, controlData)}
        />
      );
    });
  };

  return (
    <div className="authurization">
      <h1>Authurization</h1>
      <div className="form">
        <form onSubmit={submitHendler}>{renderInputs()}</form>
      </div>
      <div className='block-of-buttons'>
        <Link to = ''>
          <Button value="Sing Up" className="singUp" onClick={registrHendler} disabled={!state.isFormValid}/>
        </Link>
        <Link to = '/login'>
          <Button value="Log In" className="logIn" disabled={!state.isFormValid}/>
        </Link>
      </div>
    </div>
  );
}

export default Authorization;
