                paginPageWithPosts.map((number, index) => (

                    <button id={`${index + 1}`} key={number} onClick={() => pagFunc(dispatch(setGetPostsPage(number, postsPerPage)))} className={index + 1 === currentPage ? 'active' : 'button-pag' }>{number}</button>
                )
                )
            }
        </div>
    )
}

