import axios from "axios";
import React from "react";
import { Button } from ".";
import "../scss/components/createPost.scss";

var currentdate = new Date();
var datetime = currentdate.getDay() + "/" + currentdate.getMonth() 
+ "/" + currentdate.getFullYear() + " @ " 
+ currentdate.getHours() + ":" 
+ currentdate.getMinutes() + ":" + currentdate.getSeconds();

function CreatePost() {
  const [post, setPost] = React.useState({
    title: "",
    body: "",
    userId: "",
    createdAt: "",
    updatedAt: "",
  });

  const getCreatePost = () => {
      const data = {
          title: post.title,
          body: post.body,
          userId: '',
          createAt: Date.now(),
          updatedAt: '',
      }

      axios.post('https://ekreative-json-server.herokuapp.com/664/posts', data)
  };

    function handleChange(e) {
      setPost({ value: e.target.value });
    }

  return (
    <div className="create-post">
      <h2>create your post</h2>
      <form action="">
        <div className="title-block">
          <p className="header-for-post-form">title</p>
          <input
            type="text"
            className="input-for-title"
            value={post.title}
            onChange={handleChange}
          />
        </div>

        <div className="post-block">
          <p className="header-for-post-form">post</p>
          <textarea
            type="text"
            className="input-for-post"
            value={post.body}
            onChange={handleChange}
          ></textarea>
        </div>

        <Button type="button" className="button-create" onClick={getCreatePost}>
          Create
        </Button>
      </form>
    </div>
  );
}

export default CreatePost;
