const SET_USER_DATA = 'SET_USER_DATA';

let initialState = {
    id: null,
    email: null,
    firstname: null,
    lastname: null,
    age: null
}

const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.data
            }
        default:
            return state;
    }
}

export const setAuthUserData = (id, email, firstname, lastname, age) => ({ type: SET_USER_DATA, data: id, email, firstname, lastname, age })
export default authReducer;
