import { combineReducers, createStore, applyMiddleware } from "redux";
import thunk from 'redux-thunk'
import { postReducer, userReducer, commentReducer, paginateReducer } from "./reducer";

const rootReducer = combineReducers({ postReducer, userReducer, commentReducer, paginateReducer})

const store = createStore(
  rootReducer,
  applyMiddleware(thunk) 
)

export default store
