import axios from 'axios';
import React from 'react';
import { Button, UpdatePost } from '.';
import '../scss/components/postBlock.scss'

import { Route, Link, NavLink } from 'react-router-dom'


const PostBlock = ({ title, body, userId, createdAt }) => {
    const [usersData, setUsersData] = React.useState([])
    const [comments, setComments] = React.useState([]);


    React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
        .then(response => response.json())
        .then(data => {
            setUsersData(data)
        });
    }, []);

    function deletePost() {
        axios.delete(`https://ekreative-json-server.herokuapp.com/664/post/${userId}`)
    }



    return (
        <div className='post-block'>
            <h3>{title}</h3>
            <p>{body}</p>
            <p>{usersData.firstname}{usersData.lastname}</p>
           <Link to=''><Button>Update</Button></Link>
            {false && <UpdatePost userId={userId} title={title} body={body} createdAt={createdAt}/>}
            <Button onClick={deletePost}>Delete</Button>

        </div>
    )
}

export default PostBlock;
