import React from 'react';
import axios from 'axios';

export function getSingup(props) {
   let authData = {
      email: props.email,
      name: props.name,
      password: props.password,
      returnSecureToken: true
    }
  console.log('signup');
  if (authData.email != '') {
    axios.post('https://ekreative-json-server.herokuapp.com/register', authData)
      .then(response => this.setState({ authData: response.data.id }));
  }
}
export function getLogin (props) {

  fetch ('https://ekreative-json-server.herokuapp.com/users', {
     method: 'POST',
     body: JSON.stringify({
       email: props.email,
       password: props.password
    }),
})
  .then((response) => response.json())
  .then((result) => {
    if(result.message === 'SUCCESS'){
      alert('You are logged in.');
      this.goToMain();
     } else {
         alert('Please check your login information.');
     }
  });
}

export function getUsers () {
  let state = {
    users: []
  }
    axios.get(`https://ekreative-json-server.herokuapp.com/users`)
      .then(res => {
        console.log(res.data);
        console.log('test');
        state.users = res.data;
      });
    console.log(state.users);
}
