import React from "react";
import "./../scss/app.scss";
import { SingupForm, LoginForm } from '../conponents/index';


function Authorization() {

  const adminUser = {
    email: 'admin@admin.com',
    password: 'admin'
  }

  const [user, setUser] = React.useState({
    name: {
      type: 'firs and last name',
      value: '',
      label: 'first and last name',
      errorMessage: 'name cannot be empty',
      touched: false
    },
    email: {
      type: 'email',
      value: '',
      label: 'email',
      errorMessage: 'put correct email',
      touched: false
    },
    password: {
      type: 'password',
      value: '',
      label: 'password',
      errorMessage: 'password must have least 9 signs',
      touched: false
    },
    confirmPassword: {
      type: 'password',
      value: '',
      label: 'confirmation password',
      errorMessage: 'put correct password',
      touched: false
    },
    age: {
      type: 'age',
      value: '',
      label: 'age',
      errorMessage: 'age cannot be empty',
      touched: false
    },
    avatar: {
      type: 'url',
      value: '',
      label: 'avatar',
      errorMessage: 'avatar cannot be empty',
      touched: false
    }
  })
  const [error, setError] = React.useState('')

  return (
    <div className="authorization">
      <h1>Authorization</h1>
      {
        false
          ? <LoginForm {...user}/>
          : <SingupForm user={user}/>
        }
    </div>
  );
}

export default Authorization;
