import { ADD_DATA_TO_EDIT } from "./action";


export const postReducer = (state=[], action) => {
    switch (action.type) {
        case ADD_DATA_TO_EDIT:
            return [
                ...state,
                action.dataOfPost
            ]

        default:
            return state;
    }
}
