import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";
import "../scss/components/postBlock.scss";

var currentdate = new Date();
var datetime =
  currentdate.getFullYear() +
  "-" +
  currentdate.getMonth() +
  "-" +
  currentdate.getDay() +
  "T" +
  currentdate.getHours() +
  ":" +
  currentdate.getMinutes() +
  ":" +
  currentdate.getSeconds();

const Comments = ({ postId }) => {
  const [comments, setComments] = React.useState([]);
  const [newComment, setNewComment] = React.useState("");
  const [dataComment, setDataComment] = React.useState({});
  const [dataUserComment, setDataUserComment] = React.useState({});
  const [updateCommentBody, setUpdateCommentBody] = React.useState({
    value: "",
    openUptComm: false,
  });

    let dataAboutUser = JSON.parse(localStorage.user);

  React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/comments?postId=${postId}&_sort=createdAt&_order=asc`
    )
      .then((response) => response.json())
      .then((data) => {
        setComments(data);

        data.map((comment) =>
          setDataComment({
            id: comment.id,
            postId: comment.postId,
            body: comment.body,
            userId: comment.userId,
            createdAt: comment.createdAt,
            updatedAt: comment.updatedAt,
          })
        );
      });
  }, []);

  const handleChange = (e) => {
    setUpdateCommentBody((prevState) => ({ prevState, value: e.target.value }));
    setNewComment(e.target.value);
  };

  const openUpdateBlock = (id) => {
    setUpdateCommentBody((prevState) => ({
      prevState,
      openUptComm: true,
    }));
  };

  const updateComment = ({ id }, updateBody) => {
    const data = {
      body: updateBody,
      updatedAt: datetime,
    };
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token").slice(1, -1)}`,
    };
    axios.patch(
      `https://ekreative-json-server.herokuapp.com/664/comments/${id}`,
      data,
      { headers }
    );

    setUpdateCommentBody((prevState) => ({
      prevState,
      openUptComm: false,
    }));
  };

  const addComment = (postId) => {
    const data = {
      postId,
      userId: dataAboutUser.id,
      body: newComment,
      createdAt: datetime,
      updatedAt: "",
    };

    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token").slice(1, -1)}`,
    };



    axios.post(
      `https://ekreative-json-server.herokuapp.com/664/comments`,
      data,
      { headers }
    );
      document.location.reload(true)
  };

  const deleteComment = (id) => {
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token").slice(1, -1)}`,
    };
    axios.delete(
      `https://ekreative-json-server.herokuapp.com/664/comments/${id}`,
      { headers }
    );
      console.log(`Bearer` + localStorage.getItem("token"));
      document.location.reload(true)
  };

  // console.log('id', dataComment.userId);
   React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/users/${dataComment.userId}`
    )
      .then((response) => response.json())
      .then((data) => {
        setDataUserComment(data);
        console.log(data);
      });
  }, []);


  return (
    <div className="comments-of-post">
      <h3>comments</h3>
      {localStorage.getItem("token") ? (
        <div className="comment-input-block">
          <img
            src="https://static01.nyt.com/images/2020/11/09/arts/06depp-item/06depp-item-mediumSquareAt3X-v2.jpg"
            alt=""
          />
          <input type="text" value={newComment} onChange={handleChange} />
         <Link to='#post'><button onClick={() => addComment(postId)}>&#43;</button></Link>
        </div>
      ) : (
        ""
      )}

      {comments.map((comment) => (
        <div key={comment.id}>
          <ul>
            <div className="comment-data">
              {updateCommentBody.openUptComm ? (
                <div className="comment-content">
                  <input
                    type="text"
                    value={comment.body}
                    onChange={handleChange}
                  />
                  <button
                    onClick={() =>
                      updateComment(comment, updateCommentBody.value)
                    }
                  >
                    Update
                  </button>
                </div>
              ) : (
                  <div key={comment.id} className="comment-content">
                    <p>{dataUserComment.firstname} {dataUserComment.lastname}</p>
                  <li key={comment.id + 3 + "li"} className="comment-content">
                    <p>{comment.body}</p>

                    {localStorage.getItem('token') && dataAboutUser.id === comment.userId ? (
                      <div key={comment.id + 1}>
                        <button onClick={() => openUpdateBlock(comment.id)}>
                          Update
                        </button>
                        <button
                          key={comment.id + 2}
                          onClick={() => deleteComment(comment.id)}
                        >
                          Delete
                        </button>
                      </div>
                    ) : (
                      ""
                    )}
                  </li>
                </div>
              )}
            </div>
          </ul>
        </div>
      ))}
    </div>
  );
};

export default Comments;
