import { ADD_DATA_TO_EDIT } from "./action";


export const postReducer = (state=[], action) => {
    switch (action.type) {
        case ADD_DATA_TO_EDIT:
            const {id, title, body, userId, createdAt } = action
            return [
                ...state,
                {
                    id,
                    title,
                    body,
                    userId,
                    createdAt
                }
            ]

        default:
            return state;
    }
}
