import { applyMiddleware, combineReducers, createStore } from "redux"
import thunk from "redux-thunk"
import { paginateReducer, postReducer, userReducer, announcementsReducer, dataPostReducer, posts, commentsReducer } from "./reducer"

const rootReducer = combineReducers({paginateReducer, postReducer, userReducer, announcementsReducer, dataPostReducer, posts, commentsReducer})

export const store = createStore(rootReducer, applyMiddleware(thunk))
