import { ADD_DATA_TO_EDIT, DATA_COMMENT, DATA_USER } from "./action";


export const postReducer = (state=[], action) => {
    switch (action.type) {
        case ADD_DATA_TO_EDIT:
            const { id, title, body, userId, createdAt, key } = action
            return [
                {
                    id,
                    title,
                    body,
                    userId,
                    createdAt,
                    key
                }
            ]

        default:
            return state;
    }
}

export const userReducer = (state=[], action) => {
    switch (action.type) {
        case DATA_USER:
            const { id, email, firstname, lastname, age, avatar } = action
            console.log(email);
            return [
                {
                    id,
                    email,
                    firstname,
                    lastname,
                    age,
                    avatar
                }
            ]
        default:
            return state;
    }
}

export const commentReducer = (state=[], action) => {
    switch (action.type) {
        case DATA_COMMENT:
            const { id, postId, body, userId, createAt, updateAt } = action
            return [
                {
                    id,
                    postId,
                    body,
                    userId,
                    createAt,
                    updateAt
                }
            ]
        default:
            return state;
    }
}
