import React from "react";
import "../scss/components/announcements.scss";

const Announcement = ({ id, title, body, userId }) => {
  const [usersData, setUsersData] = React.useState([]);
  const [editAnnoun, setEditAnnoun] = React.useState({
    valueTitle: '',
    valueBody: '',
    openUptAnnoun: false,
    id: ''
  })

  let dataAboutUser = JSON.parse(localStorage.user);

  React.useEffect(() => {
    getDataUserAnnoun()
  }, []);

  const getDataUserAnnoun = () => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUsersData(data);
      });
  };

  const openEditBlock = () => {
    setEditAnnoun((prevState) => ({
      ...prevState,
      openUptAnnoun: true,
    }))
  }

  return (
    editAnnoun.openUptAnnoun ? (
    <div>
      <input type="text" />
      <textarea />
      <button onClick={() => {}} className="button-edit-announ">
            Edit
          </button>
    </div>
  ) : (
    <div key={id} className="announ">
      <div>
        <h3>{title}</h3>
        <p className="body-announ">{body}</p>
      </div>
      <div className="buttom-announ">
         <p>
          {usersData.firstName} {usersData.lastname}
        </p>
        {localStorage.getItem(
          "token"
        ) && dataAboutUser.id === id  ? (
          <button onClick={() => openEditBlock()} className="button-edit-announ">
            Edit
          </button>
        ) : (
          ""
        )}

      </div>
    </div>)
  );
};

export default Announcement;
