import axios from 'axios';
import React from 'react';
import { Button, UpdatePost } from '.';
import '../scss/components/postBlock.scss'

import { Route, Link, NavLink } from 'react-router-dom'


const PostBlock = ({ id, title, body, userId, createdAt }) => {
    const [usersData, setUsersData] = React.useState([])
    const [comments, setComments] = React.useState([]);
    const [dataPost, setDataPost] = React.useState({});


    React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
        .then(response => response.json())
        .then(data => {
            setUsersData(data)
        });
    }, []);

    React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/comments?postId=${id}&_sort=createdAt&_order=asc

`)
        .then(response => response.json())
        .then(data => {
            setComments(data)
        });
    }, []);

    function deletePost() {
        axios.delete(`https://ekreative-json-server.herokuapp.com/664/post/${userId}`)
    }

    function sendState() {
        setDataPost({
            title: title,
            body: body,
            userId: userId,
            createdAt: createdAt,})
    }

    return (
        <div className='post-block'>
            <div>
            <h3>{title}</h3>
            <p>{body}</p>
                <p>{usersData.firstname} {usersData.lastname}</p>
            </div>

            <div>
                <p>{comments}</p>
            </div>

           <Link to='/update'><Button className='button-post' onClick={sendState}>Edit</Button></Link>
            {/* {false && <UpdatePost userId={userId} title={title} body={body} createdAt={createdAt}/>} */}
            <Button className='button-post' onClick={deletePost}>Delete</Button>

        </div>
    )
}

export default PostBlock;
