import axios from "axios";
import React from "react";
import { Button, Input } from "../conponents";

function Login() {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");

  function getLogin() {
    const data = {
      email: email,
      password: password,
    };

    axios.post("https://ekreative-json-server.herokuapp.com/login", data)
  }

  return (
    <div className="authorization">
      <h2>Log In</h2>
      <div className="form">
        <form method='post'>
          <Input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="text"
            label="email"
            // placeholder="E-mail Address"
          />
          <Input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            label="password"
            // placeholder="Password"
          />
          <Button type="button" onClick={getLogin}>
            login
          </Button>
        </form>
      </div>
    </div>
  );
}

export default Login;
