import axios from 'axios';
import React from 'react';
import { Button, UpdatePost } from '.';
import '../scss/components/postBlock.scss'

import { Link, NavLink } from 'react-router-dom'


const PostBlock = ({ title, body, userId }) => {
    const [usersData, setUsersData] = React.useState([])

    React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
        .then(response => response.json())
        .then(data => {
            setUsersData(data)
        });
    }, []);

    const undateFunction = () => {
        <UpdatePost userId={userId}/>
    }

    return (
        <div className='post-block'>
            <h3>{title}</h3>
            <p>{body}</p>
            <p>{usersData.firstname}{usersData.lastname}</p>
            <Link to='/udate'><Button onClick={undateFunction}>Update</Button></Link>
        </div>
    )
}

export default PostBlock;
