import React from 'react';
import { Input } from '../conponents';


function Login(params) {

    const [user, setUser] = React.useState({
        formData: {
            email: {
                type: "email",
                value: "",
                label: "email",
                errorMessage: "put correct email",
                valid: false,
                touched: false,
            },
            password: {
                type: "password",
                value: "",
                label: "password",
                errorMessage: "password must have least 9 signs",
                valid: false,
                touched: false,
            },
        }
    })

    const renderInputs = () => {
    return Object.keys(user.formData).map((dataName, index) => {
      const data = user.formData[dataName];
      return (
        <Input
          key={dataName + index}
          type={data.type}
          value={data.value}
          valid={data.valid}
          touched={data.touched}
          label={data.label}
          shouldValidate={!!data.validation}
          errorMessage={data.errorMessage}
        //   onChange={(event) => onChangeHandler(event, dataName)}
        />
      );
    });
  };


    return (
        <div className="authorization">
        <h2>Login</h2>
      <div className="form">
        <form>{renderInputs()}</form>
      </div>
    </div>
    )
}

export default Login;
