import React from "react";
import { Input, Button } from "../conponents/index";
import axios from "axios";

const emailRegex = RegExp(/^[a-zA-Z0-9.! #$%&'*+/=? ^_`{|}~-]+@[a-zA-Z0-9-]+(?:\. [a-zA-Z0-9-]+)*$/)

const formValid = formErrors => {
  let valid = true

  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false)
  });

  return valid
}

function Singup() {
  const [user, setUser] = React.useState({
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    confirPass: '',
    age: '',
    avatar: '',
    formErrors: {
      firstname: '',
      lastname: '',
      email: '',
      password: '',
      confirPass: '',
      age: '',
      avatar: ''
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault()
    formValid(user.formErrors)
  }

  const handleChange = e => {
    e.preventDefault()
    const { name, value } = e.target;
    let formErrors = user.formErrors;


    switch (name) {
      case 'firstname':
        formErrors.firstname = value === ''
          ? 'this field cant be empty'
          : ''
        break;
      case 'lastname':
        formErrors.lastname = value === ''
          ? 'this field cant be empty'
          : ''
        break;
      case 'email':
        formErrors.email =
        emailRegex.test(value) && value.length !== ''
          ? ''
          : 'invalid email address'
        break;
      case 'password':
        formErrors.password = value.length < 12
          ? 'this field must have least 12 signs'
          : ''
        break;
      case 'confirPass':
        formErrors.confirPass = value === formErrors.password
          ? 'passwords not match'
          : ''
        break;
      case 'age':
        formErrors.age = value === ''
          ? 'this field cant be empty'
          : ''
        break;
      case 'avatar':
        formErrors.avatar = value === ''
          ? 'this field cant be empty'
          : ''
        break;
      default:
        break;
    }
    setUser({ formErrors, [name]: value})
  }

  function getSingup() {
    const authData = {
      email: user.email,
      password: user.password,
      firstname: user.firstname,
      lastname: user.lastname,
      age: user.age,
    };
    axios.post("https://ekreative-json-server.herokuapp.com/register", authData);
  }


  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            value={user.firstname}
            onChange={handleChange}
            label="first name"
            name="firstname"
            formErrors={user.formErrors.firstname}
          />
          <Input
            type="text"
            value={user.lastname}
            onChange={handleChange}
            label="last name"
            name="lastname"
            formErrors={user.formErrors.lastname}
          />
          <Input
            type="email"
            value={user.email}
            onChange={handleChange}
            formErrors={user.formErrors.email}
            label="email"
            name="email"
          />
          <Input
            type="password"
            value={user.password}
            onChange={handleChange}
            formErrors={user.formErrors.password}
            label="password"
            name="password"
          />
          <Input
            type="password"
            value={user.confirPass}
            onChange={handleChange}
            formErrors={user.formErrors.confirPass}
            label="cofirmation password"
            name="cofirmationPassword"
          />
          <Input
            type="number"
            value={user.age}
            onChange={handleChange}
            formErrors={user.formErrors.age}
            label="age"
            name="age"
          />
          <Input
            type="text"
            value={user.avatar}
            onChange={handleChange}
            formErrors={user.formErrors.avatar}
            label="avatar"
            name="avatar"
          />
          <Button type="button" onClick={getSingup}>
            Singup
          </Button>
        </form>
      </div>
    </div>
  );
}

export default Singup;
