// import './App.css';
import React from 'react';
import { useDispatch } from 'react-redux';
import { Route, Routes } from 'react-router';
import AnnounEdit from './components/AnnounEdit.jsx';
import CreateAnnouncement from './components/CreateAnnuncement.jsx';
import { CreatePost, GetPosts, UserPage } from './components/index.js'
import Post from './components/Post.jsx';
import { Home, Login, Signup } from './pages'
import { setDataAnnouncements, setPosts } from './redux/action.js';

function App() {

  const dispatch = useDispatch()


 React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/664/announcements?_sort=createdAt&_order=desc&_limit=10`
    )
      .then((response) => response.json())
      .then((data) => {
        // dispatch(setDataAnnouncements(data))
        console.log(data);
      })
  }, []);



  React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/664/posts`)
      .then((response) => response.json())
      .then((data) => {
        dispatch(setPosts(data))
      });
  }, []);

  return (
    <div className="wrapper">
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/login' element={<Login />} />
        <Route path='/signup' element={<Signup />} />
        <Route path='/create' element={<CreatePost />} />
        <Route path='/create-announ' element={<CreateAnnouncement />} />
        <Route path='/post/:postId' element={<Post />} />
        <Route path='/edit-announ' element={<AnnounEdit />} />
        <Route path='/user-page' element={<UserPage />} />
      </Routes>
    </div>
  );
}

export default App;
