import React from "react";
import "./../scss/app.scss";
import { SingupForm, LoginForm } from '../conponents/index';


function Authorization() {

  const adminUser = {
    email: 'admin@admin.com',
    password: 'admin'
  }

  const [user, setUser] = React.useState({
    name: {
      type: 'firs and last name',
      value: '',
      label: 'first and last name'
    },
    email: {
      type: 'email',
      value: '',
      label: 'email'
    },
    password: {
      type: 'password',
      value: '',
      label: 'password'
    },
    confirmPassword: {
      type: 'password',
      value: '',
      label: 'confirmPassword'
    },
    age: {
      type: 'age',
      value: '',
      label: 'age'
    },
    avatar: {
      type: 'url',
      value: '',
      label: 'avatar'
    }
  })
  const [error, setError] = React.useState('')

  const Login = details => {
    console.log(details);

    if (details.email === adminUser.email && details.password === adminUser.password) {
      console.log('Logged in');

      setUser({
        name: details.name,
        email: details.email
      })
    } else {
      console.log('Details do not match!');
    }
  }

  const Logout = () => {
    console.log('Logout');
  }

  const Singup = () => {
    console.log('Singup');
  }



  return (
    <div className="authorization">
      <h1>Authorization</h1>
      {
        false
          ? <LoginForm {...user} Login={Login} error={error} />
          : <SingupForm user={user} Singup={Singup} error={error} />
        }
    </div>
  );
}

export default Authorization;
