import React from "react";
import { Link } from "react-router-dom";
import { Button, Input } from "../conponents";

function Login(props) {
  const [state, setState] = React.useState({
    formData: {
      email: {
        value: "",
        type: "email",
        label: "email",
        errorMessage: "enter correct email",
        valide: false,
        touched: false,
        validation: {
          required: true,
          email: true,
        },
      },
      password: {
        value: "",
        type: "text",
        label: "password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          password: 9,
        },
      }
    }
  });

  const loginHendler = async () => {
    const authData = {
      email: state.formData.email.value,
      password: state.formData.password.value,
      returnSecureToken: true
    }

     axios.post('https://identitytoolkit.googleapis.com/v1/accounts:signIn?key=AIzaSyABuCCU2ip1w5leYsAv2F8YTEt0G8B66Sw', authData)
      .catch((e) => {
        console.log(e);
      })
  };

  const renderInputsLogin = () => {
    return Object.keys(state.formData).map((controlData, index) => {
      const data = state.formData[controlData];

      return (
        <Input
          key={controlData + index}
          value={data.value}
          type={data.type}
          label={data.label}
          errorMessage={data.errorMessage}
          valide={data.valide}
          shouldValidate={!!data.validation}
          touched={data.touched}
          // onChange={(event) => changeInput(event, controlData)}
        />
      );
    })
  }

  return (
    <div className="authurization">
      <h1>Log In</h1>
      <div className="form">
        <form>{renderInputsLogin()}</form>
      </div>
      <div className="block-of-buttons">
        <Link to="">
          <Button value="Log In" className="singUp" onChange={loginHendler}/>
        </Link>
        <Link to="/">
          <Button value="Sing Up" className="logIn" />
        </Link>
      </div>
    </div>
  );
}

export default Login;
