import axios from "axios";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button, PostBlock } from ".";
import { setAddData } from "../redux/action";
import "../scss/components/getPosts.scss";

var currentdate = new Date();

var datetime =
  currentdate.getFullYear() +
  "-" +
  currentdate.getMonth() +
  "-" +
  currentdate.getDay() +
  "T" +
  currentdate.getHours() +
  ":" +
  currentdate.getMinutes() +
  ":" +
  currentdate.getSeconds();

const GetPosts = () => {
  const [posts, setPosts] = React.useState([]);
  const [isLoaded, setIsLoaded] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [updateDataPost, setUpdateDataPost] = React.useState(null);

  const dispatch = useDispatch()

  const postReducer = useSelector(state => state.postReducer)
  console.log(postReducer);

  React.useEffect(() => {
    fetch("https://ekreative-json-server.herokuapp.com/664/posts")
      .then((response) => response.json())
      .then((data) => {
        setPosts(data);
      });
  }, []);

  const getUpdatePost = () => {
    posts.map((dataPost) => {
      setUpdateDataPost({
        title: dataPost.title,
        body: dataPost.body,
        userId: dataPost.userId,
        createAt: dataPost.createdAt,
        updatedAt: datetime
      });
    });

    axios.patch(
      `https://ekreative-json-server.herokuapp.com/664/post/`,
      updateDataPost
    );
  };

  const handleChange = (e) => {
    e.preventDefault()
    dispatch(setAddData())
  }



  return (
    <div className="get-posts">
      {posts.map((dataPost) => (
        <PostBlock key={dataPost.id} {...dataPost} />
      ))}
      {postReducer.map((dataPost) => (
        <div id="modal" className="update-post" key={dataPost.id}>
          <div className="close">
            <span className="material-icons-outlined">&times;</span>
          </div>
          <div className='modal-content'>
            <h2>update your post</h2>
            <form action="">
              {/* <div className="title-block"> */}
              <p className="header-for-post-form">title</p>
              <input
                type="text"
                className="input-for-title"
                value={dataPost.title}
                onChange={handleChange}
              />
              {/* </div> */}

              {/* <div className="post-block"> */}
              <p className="header-for-post-form">post</p>
              <textarea
                type="text"
                className="input-for-post"
                value={dataPost.body}
                onChange={handleChange}
              >
                {dataPost.body}
              </textarea>
              {/* </div> */}

              <Button
                type="button"
                className="button-update"
                onClik={getUpdatePost}
              >
                Edit
              </Button>
              </form>
            </div>
        </div>
      ))}
      <span>&times;</span>
    </div>
  );
};

export default GetPosts;
