import React from "react";
import { Link } from "react-router-dom";
import { Button, Input } from "../conponents";

function Login(props) {
  const [state, setState] = React.useState({
    formData: {
      email: {
        value: "",
        type: "email",
        lable: "email",
        errorMessage: "enter correct email",
        valide: false,
        touched: false,
        validation: {
          required: true,
          email: true,
        },
      },
      password: {
        value: "",
        type: "text",
        label: "password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          password: 9,
        },
      }
    }
  });

  const renderInputsLogin = () => {
    return Object.keys(state.formData).map((controlData, index) => {
      const data = state.formData[controlData];

      return (
        <Input
          key={controlData + index}
          value={data.value}
          type={data.type}
          label={data.label}
          errorMessage={data.errorMessage}
          valide={data.valide}
          shouldValidate={!!data.validation}
          touched={data.touched}
          onChange={(event) => changeInput(event, controlData)}
        />
      );
    })
  }

  return (
    <div className="authurization">
      <h1>Log In</h1>
      <div className="form">
        <form>{renderInputsLogin()}</form>
      </div>
      <div className="block-of-buttons">
        <Link to="">
          <Button value="Log In" className="singIn" />
        </Link>
        <Link to="/">
          <Button value="Sing In" className="logIn" />
        </Link>
      </div>
    </div>
  );
}

export default Login;
