import { ADD_DATA_TO_EDIT, DATA_USER } from "./action";


export const postReducer = (state=[], action) => {
    switch (action.type) {
        case ADD_DATA_TO_EDIT:
            const { id, title, body, userId, createdAt, key } = action
            return [
                {
                    id,
                    title,
                    body,
                    userId,
                    createdAt,
                    key
                }
            ]

        default:
            return state;
    }
}

