import React from "react";
import { Input, Button } from "../conponents/index";
import axios from "axios";

function Singup() {
  const [firstname, setFirstname] = React.useState("");
  const [lastname, setLastname] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [confirPass, setConfirPass] = React.useState("");
  const [age, setAge] = React.useState("");
  const [avatar, setAvatar] = React.useState("");

  function getSingup() {
    const authData = {
      email: email,
      password: password,
      firstname: firstname,
      lastname: lastname,
      age: age,
    };
    axios.post("https://ekreative-json-server.herokuapp.com/users", authData);
  }

  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form>
          <Input
            type="text"
            value={firstname}
            onChange={(e) => setFirstname(e.target.value)}
            label="first name"
          />
          <Input
            type="text"
            value={lastname}
            onChange={(e) => setLastname(e.target.value)}
            label="last name"
          />
          <Input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            label="email"
          />
          <Input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            label="password"
          />
          <Input
            type="text"
            value={confirPass}
            onChange={(e) => setConfirPass(e.target.value)}
            label="cofirmation password"
          />
          <Input
            type="number"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            label="age"
          />
          <Input
            type="text"
            value={avatar}
            onChange={(e) => setAvatar(e.target.value)}
            label="avatar"
          />
          <Button type="button, submit" onClick={getSingup}>
            Singup
          </Button>
        </form>
      </div>
    </div>
  );
}

export default Singup;
