import React from 'react';
import axios from 'axios';

export const getSingup = (props) => {
    const authData = {
      email: props.email,
      name: props.name,
      password: props.password,
      returnSecureToken: true
    }

  axios.post('https://ekreative-json-server.herokuapp.com/users', authData)
    .then(response => this.setState({ authData: response.data.id }));
}
export const getLogin = (props) => {
    const authData = {
      email: props.email,
      password: props.password,
      returnSecureToken: true
    }

  axios.post('https://ekreative-json-server.herokuapp.com/users', authData)
    .then(response => this.setState({ authData: response.data.id }));
}
