import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";
import { Button } from "./index";
import "../scss/components/createPost.scss";
import { setPosts } from "../redux/action";
import { useDispatch } from "react-redux";

var currentdate = new Date();
var datetime =
  currentdate.getFullYear() +
  "-" +
  currentdate.getMonth() +
  "-" +
  currentdate.getDay() +
  "T" +
  currentdate.getHours() +
  ":" +
  currentdate.getMinutes() +
  ":" +
  currentdate.getSeconds();

function CreatePost() {
  const [post, setPost] = React.useState({
    title: "",
    body: "",
  });

  const dispatch = useDispatch()

  let dataAboutUser = JSON.parse(localStorage.user);

  const getCreatePost = async () => {
    const data = {
      title: post.title,
      body: post.body,
      userId: dataAboutUser.id,
      createAt: datetime,
      updatedAt: datetime,
    };

    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token").slice(1, -1)}`,
    };

    await axios.post("https://ekreative-json-server.herokuapp.com/664/posts", data, {
      headers,
    });

    await axios.get("https://ekreative-json-server.herokuapp.com/664/posts")
    .then(response =>  dispatch(setPosts(response.data)))

    // Location.reload(true)
  };

  function handleChange(e) {
    const { name, value } = e.target;
    setPost((prevState) => ({ ...prevState, [name]: value }));
  }

  return (
    <div className="create-post">
      <h2>create your post</h2>
      <form action="">
        <div className="title-block">
          <input
            type="text"
            className="input-for-title"
            value={post.title}
            name="title"
            placeholder="title of your post"
            onChange={handleChange}
          />
          <textarea
            type="text"
            className="input-for-post"
            value={post.body}
            name="body"
            placeholder="write here your story or dream or somthing else..."
            onChange={handleChange}
          ></textarea>
        </div>

        <Link to='/'
            type="button"
            className="button-create"
            onClick={() => getCreatePost()}
          >
            Create
        </Link>
      </form>
     <Link to='/' className="logo"><h2 className="logo">Tell <span>me</span></h2></Link>
    </div>
  );
}

export default CreatePost;
