import { render } from "node-sass";
import React from "react";
// import axios from "axios";

import { Routes, Route } from "react-router-dom";
import { AuthApi, Button } from "./conponents";
import { Registration, Login, Singup, FormLogin, AuthApir } from "./pages/index";

function App() {

  const [auth, setAuth] = React.useState(false)

  const Auth = React.createContext(AuthApi)

  return (
    <div className="wrapper">
      <div>Wrapper</div>
      <div className="content">
        <AuthApi.Provider value={auth, setAuth}>
        <Routes>
        <Route path = '/singup' element = {<Singup />} />
        {/* <Route path = '/login' element = {<Login />} /> */}
        <Route path = '/login' element = {<FormLogin />} />
        <ProtectRoute path = '/dashboard' auth={A} element = {<Dashboard />} />
      </Routes>
      </AuthApi.Provider>
      </div>
    </div>
  );
}

const Dashboard = () => {
  return (
    <div>
      <h1>Dashbooard</h1>
      <Button>Logout</Button>
    </div>
  )
}

const ProtectRoute = ({ auth, component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={() => {
        <Component />
      }}
    />
  )
}

export default App;
