import classNames from "classnames";
import React, { isValidElement } from "react";
// import { _input } from './../scss/components/_all.scss'

function isInvalid({ valide, touched, shouldValidate }) {
    return !valide && shouldValidate && touched;
}

function Input(props) {

    const inputType = props.type || 'text';
    const htmlFor = `${inputType}-${Math.random()}`

  return (
    <div className={classNames('input', isInvalid(props) ? 'invalid' : '')}>
      <label htmlFor={htmlFor}>{props.label}</label>
          <input
              type={inputType}
              id={htmlFor}
              value={props.value}
              onChange={props.onChange}
          />

          {
              isInvalid(props)
                  ? <span>{props.errorMessage || 'enter the correct value'}</span>
                  : null
            }

    </div>
  );
}

export default Input;
