import axios from 'axios';
import React from 'react'
import { Button, Input } from '../conponents';
import '../scss/app.scss'

export default function Registration() {

    const [state, setState] = React.useState({
        firstname: '',
        lastname: '',
        email: '',
        password: '',
        confirmPassword: '',
        age: '',
        avatar: '',
        registrationError: ''
    })

    const handleChange = (event) => {
        setState({
            [event.target.name]: event.target.value
        })
    }

    const handleSubmit = (event) => {
        axios.post('http://localhost:3004')
        event.preventDefault();
    }

    return (
        <div className='authorization'>
            <h2>Sing Up</h2>
            <div className='form'>
            <form onSubmit={handleSubmit}>
                <Input
                    type='text'
                    label='first name'
                    name='first name'
                    value={state.firstname}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='text'
                    label='last name'
                    name='last name'
                    value={state.lastname}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='email'
                    label='email'
                    name='email'

                    value={state.email}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='password'
                    label='password'
                    name='password'

                    value={state.password}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='password'
                    label='confirmation password'
                    name='confirmPassword'

                    value={state.confirmPassword}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='number'
                    label='age'
                    name='age'

                    value={state.age}
                    onChange={handleChange}
                    required
                />
                <Input
                    type='url'
                    label='avatar'
                    name='avatar'
                    value={state.avatar}
                    onChange={handleChange}
                    required
                />
                </form>
                </div>
                <Button type='submit'/>
        </div>
    )
}
