import React from "react";
import { Input, Button } from "../conponents/index";
import axios from "axios";

function Singup() {
  const [user, setUser] = React.useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    confirPassword: "",
    age: "",
    age: "",
    avatar: "",
  });

  function getSingup() {
    const authData = {
      email: user.email,
      password: user.password,
      firstname: user.firstname,
      lastname: user.lastname,
      age: user.age,
    };
    axios.post('https://ekreative-json-server.herokuapp.com/users', authData);
  }

  function handleChange(event) {
    setUser({ value: event.target.value });
  }

  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form>
          <Input
            type="text"
            value={user.firstname}
            onChange={handleChange}
            label="first name"
          />
          <Input
            type="text"
            value={user.lastname}
            onChange={handleChange}
            label="last name"
          />
          <Input
            type="email"
            value={user.email}
            onChange={handleChange}
            label="email"
          />
          <Input
            type="password"
            value={user.password}
            onChange={handleChange}
            label="password"
          />
          <Input
            type="password"
            value={user.confirPassword}
            onChange={handleChange}
            label="cofirmation password"
          />
          <Input
            type="number"
            value={user.age}
            onChange={handleChange}
            label="age"
          />
          <Input
            type="text"
            value={user.avatar}
            onChange={handleChange}
            label="avatar"
          />
          <Button type="button" onClick={getSingup}>
            Singup
          </Button>
        </form>
      </div>
    </div>
  );
}

export default Singup;
