  const [details, setDetails] = React.useState({
    email: {
      type: 'email',
      value: '',
      label: 'email'
    },
    password: {
      type: 'password',
      value: '',
      label: 'password'
    }
  })

  const submitHendler = e => {
    e.preventDefault();

    Login(details)
  }

  const renderInput = () => {
    return Object.keys(details).map((dataIput, index) => {
      const data = details[dataIput]

      return (
        <Input
          type={data.type}
          value={data.value}
          label={data.label}
        />
      )
    })
  }

  return (
    <div className='authorization'>
          <div className="form">
              <h2>Sing Up</h2>
        <form>
          <Input
            type={props.type}
            value={props.value}
            lable={props.lable}
          />
        </form>
      </div>
      <div className="block-of-buttons">
          <Button
            value="Sing Up"
            className="singUp"
            // disabled={!state.isFormValid}
          />
          <Button
            value="Log In"
            className="logIn"
            // disabled={!state.isFormValid}
          />
      </div>
    </div>
  );
}


export default SingupForm;
