export const ADD_DATA_TO_EDIT = "EDIT";
export const DATA_USER = "DATA_USER";
export const DATA_COMMENT = "DATA_COMMENT";
export const GET_POSTS_PAGE = "GET_POSTS_PAGE";

export const setAddData = (id, title, body, userId, createdAt) => ({
  type: ADD_DATA_TO_EDIT,
  id,
  title,
  body,
  userId,
  createdAt,
});

export const setDataUser = ({
  id,
  email,
  firstname,
  lastname,
  age,
  avatar,
}) => (
  console.log(email),
  {
    type: DATA_USER,
    id,
    email,
    firstname,
    lastname,
    age,
    avatar,
  }
);

export const setDataComment = ({
  id,
  postId,
  body,
  userId,
  createAt,
  updateAt,
}) => ({
  type: DATA_COMMENT,
  id,
  postId,
  body,
  userId,
  createAt,
  updateAt,
});

