import axios from "axios";
import React from "react";
import "../scss/components/postBlock.scss";
import "../scss/components/getPosts.scss";

// import { Route, Link, NavLink } from "react-router-dom";
import classNames from "classnames";
import { setAddData } from "../redux/action";

const PostBlock = ({ id, title, body, userId, createdAt }) => {
  const [usersData, setUsersData] = React.useState([]);
  const [comments, setComments] = React.useState([]);
  const [dataComment, setDataComment] = React.useState({});
  const [dataUserComment, setDataUserComment] = React.useState({});

  const dispatch = useDispatch();

  console.log('user data', dataUserComment);
  React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUsersData(data);
      });
  }, []);

  React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/comments?postId=${id}&_sort=createdAt&_order=asc`
    )
      .then((response) => response.json())
      .then((data) => {
        setComments(data);
        data.map((comment) =>
            setDataComment({
              id: comment.id,
              postId: comment.postId,
              body: comment.body,
              userId: comment.userId,
              createdAt: comment.createdAt,
              updatedAt: comment.updatedAt,
            })
        );
      });
  }, []);

  // console.log('dataComment', dataComment.userId);

    React.useEffect(() => {
  fetch(`https://ekreative-json-server.herokuapp.com/users/${dataComment.userId}`)
    .then((response) =>response.json())
    .then((data) => { 
      setDataUserComment(data)
      // console.log(data);
    }
    );
  }, [])

  function deletePost() {
    axios.delete(
      `https://ekreative-json-server.herokuapp.com/664/post/${id}`
    );
  }

  const editPost = (id, title, body, userId, createdAt) => {
    dispatch(setAddData(id, title, body, userId, createdAt));
  };

  let data = JSON.parse(localStorage.user);

  return (
    <div className="post-block">
      <div className="content-post">
        <div className="content">
          <h3>{title}</h3>
          <p>{body}</p>
          <p>
            {usersData.firstname} {usersData.lastname}
          </p>
        </div>
        {localStorage.getItem("token") && usersData.id === data.id ? (
          <div className="content-button">
            <button
              className={classNames("button-post", "button")}
              onClick={() => editPost(id, title, body, userId, createdAt)}
            >
              Edit
            </button>
            <button
              className={classNames("button-post", "button")}
              onClick={deletePost}
            >
              Delete
            </button>
          </div>
        ) : (
          ""
        )}
      </div>

      <div className="comments-of-post">
        <h3>comments</h3>
        {comments.map((comment) => (
          <div key={comment.id}>
            <ul>
              {comment.userId === dataUserComment.id ? (
                <div>
                  <img src={dataUserComment.avatar} alt="" />
                  <p>{dataUserComment.firstname} {dataUserComment.lastname}</p>
                </div>
              ) : (
                ""
              )}
              <li className="comment">{comment.body}</li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostBlock;
