import axios from "axios";
import React from "react";
import "../scss/components/postBlock.scss";
import "../scss/components/getPosts.scss";

// import { Route, Link, NavLink } from "react-router-dom";
import classNames from "classnames";
import { setAddData } from "../redux/action";
import { useDispatch } from "react-redux";

const PostBlock = ({ id, title, body, userId, createdAt }) => {
  const [usersData, setUsersData] = React.useState([]);
  const [comments, setComments] = React.useState([]);
  const [dataComment, setDataComment] = React.useState({});
  const [dataUserComment, setDataUserComment] = React.useState({});

  const dispatch = useDispatch();

  // console.log('user data', dataUserComment);
  React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUsersData(data);
      });
  }, []);

  React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/comments?postId=${id}&_sort=createdAt&_order=asc`
    )
      .then((response) => response.json())
      .then((data) => {
        setComments(data);
        data.map((comment) =>
          setDataComment({
            id: comment.id,
            postId: comment.postId,
            body: comment.body,
            userId: comment.userId,
            createdAt: comment.createdAt,
            updatedAt: comment.updatedAt,
          })
        );
      });
  }, []);

  // console.log('id', dataComment.userId);

  // React.useEffect(() => {
  //   fetch(
  //     `https://ekreative-json-server.herokuapp.com/users/${dataComment.userId}`
  //   )
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setDataUserComment(data);
  //       // console.log(data);
  //     });
  // }, []);

  function deletePost() {
    axios.delete(`https://ekreative-json-server.herokuapp.com/664/post/${id}`);
  }

  const editPost = (id, title, body, userId, createdAt) => {
    dispatch(setAddData(id, title, body, userId, createdAt));
  };

  const addComment = () => {
    axios.post(`https://ekreative-json-server.herokuapp.com/664/comments`);
  };
  const updateComment = (idUpdateComment) => {
    axios.post(
      `https://ekreative-json-server.herokuapp.comPATCH /664/comments/${idUpdateComment}`
    );
  };

  let data = JSON.parse(localStorage.user);

  return (
    <div className="post-block">
      <div className="content-post">
        <div className="content">
          <h3>{title}</h3>
          <p>{body}</p>
          <p>
            {usersData.firstname} {usersData.lastname}
          </p>
        </div>
        {localStorage.getItem("token") && usersData.id === data.id ? (
          <div className="content-button">
            <button
              className={classNames("button-post", "button")}
              onClick={() => editPost(id, title, body, userId, createdAt)}
            >
              Edit
            </button>
            <button
              className={classNames("button-post", "button")}
              onClick={deletePost}
            >
              Delete
            </button>
          </div>
        ) : (
          ""
        )}
      </div>

      <div className="comments-of-post">
        <h3>comments</h3>
        {localStorage.getItem("token") ? (
          <div className="comment-input-block">
            <img
              src="https://static01.nyt.com/images/2020/11/09/arts/06depp-item/06depp-item-mediumSquareAt3X-v2.jpg"
              alt=""
            />
            <input type="text" />
            <button onClick={addComment}>&#43;</button>
          </div>
        ) : (
          ""
        )}

        {comments.map((comment) => (
          <div key={comment.id}>
            <ul>
              <div className="comment-data">
                {false ? (
                  <div className="comment-content">
                    <input type="text" value={comment.body} />
                    <button onClick={() => updateComment(comment.id)}>
                      Update
                    </button>
                  </div>
                ) : (
                  <div className="comment-content">
                    <li>{comment.body}</li>

                    <button onClick={() => updateComment(comment.id)}>
                      Update
                    </button>
                  </div>
                )}
              </div>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostBlock;
